mapper.select:
select * from review
where goods_id = #{goods_id}
order by review_asc desc

select * from review
where goods_id = #{goods_id}
order by Likes_count desc

select * from review
where goods_id = #{goods_id} and user_id = #{user_id}
order by review_asc desc

mapper.update:
update review
set review_viewCount = review_viewCount + 1
where review_id = #{review_id}

mapper.select:
select max(review_asc)
from review
where goods_id = #{goods_id}
